#!/usr/bin/env bash
## Mojave Sample Connector (FSP1)

# Environment variables hard-coded using export statements (as requested)

# =====================
# FSPIOP participant
# =====================
export FSPIOP_FSP_CODE="fsp1"
export FSPIOP_FSP_NAME="FSP 1"
export FSPIOP_ILP_SECRET="ilp_secret_!!"
export FSPIOP_SIGN_JWS="true"
export FSPIOP_VERIFY_JWS="true"

# Comma-separated list of other FSPs you interact with
export FSPIOP_FSPS="hub,fsp2,fsp3"

# Private key for this FSP (PEM as single line)
export FSPIOP_PRIVATE_KEY_PEM="-----BEGINPRIVATEKEY----------ENDPRIVATEKEY-----"

# Public keys of other FSPs (names must match FSPIOP_FSPS)
export FSPIOP_PUBLIC_KEY_PEM_OF_HUB="-----BEGINPUBLICKEY----------ENDPUBLICKEY-----"
export FSPIOP_PUBLIC_KEY_PEM_OF_FSP2="-----BEGINPUBLICKEY----------ENDPUBLICKEY-----"
export FSPIOP_PUBLIC_KEY_PEM_OF_FSP3="-----BEGINPUBLICKEY----------ENDPUBLICKEY-----"

# =====================
# Inbound (server/Tomcat)
# =====================
export FSPIOP_INBOUND_PORT="8080"
export FSPIOP_INBOUND_MAX_THREAD="200"
export FSPIOP_INBOUND_CONNECTION_TIMEOUT="30000"

# mTLS for inbound
export FSPIOP_INBOUND_USE_MUTUAL_TLS="false"
# mTLS for Invoker
export INVOKER_USE_MUTUAL_TLS="false"
# If true, also set the following
export KEYSTORE_P12B64_CONTENT=""
export KEYSTORE_PASSWORD="fsp1"
export TRUSTSTORE_P12B64_CONTENT=""
export TRUSTSTORE_PASSWORD="fsp1"
#export TRUSTSTORE_P12B64_CONTENT="MII...(4273 characters truncated, it's impossible to view or edit this line)"
#export TRUSTSTORE_PASSWORD="all"

# =====================
# Outbound
# =====================
export FSPIOP_OUTBOUND_PORT="8081"
export FSPIOP_OUTBOUND_MAX_THREAD="200"
export FSPIOP_OUTBOUND_CONNECTION_TIMEOUT="30000"

export FSPIOP_OUTBOUND_SECURED="false"
export FSPIOP_OUTBOUND_PUBLIC_KEY_PEM="-----BEGIN PUBLIC KEY----- MIIBIjANBgkqhkiG9w0BAQEFAAOCAQ8AMIIBCgKCAQEAr+f0vHYEtU4z+VCFWCA5 5Y19S6kEktXRXnUCyzLOxjuIV2alov2MwuAbOYe6LrvazkGZtzTzi1+S/noxBvJX tDAxM9qKXLMuq+pmku65dWWBUyQt4uRkpu6a+V8zK73aJM5LycgmXDwWN5fNzN5l rTRdiwMVB9SrekqhnJjIBWO8E4tZwY4j+Qr1vSRj4PxVLMxIktxQViUSrFn2WVfF M5wZset33rGQSgZv9TYFKAlQA3DZ9yGJWR7h/tz6deLu5tX0o/L20RsFJZ4RwBZL 4qCwhM7+cTv94/UWYWUL0imnm55w6aFS+YbpAwlJPNi4p2mtTnvquWjEUfZ+r4Bh 6wIDAQAB -----END PUBLIC KEY-----"
export FSPIOP_OUTBOUND_PUBSUB_TIMEOUT="4000"
export FSPIOP_OUTBOUND_PUT_RESULT_TIMEOUT="3000"

# Expiry for transfers initiated by outbound
export FSPIOP_OUTBOUND_EXPIRE_AFTER_SECONDS="900"

# =====================
# FSPIOP external service endpoints
# =====================
export FSPIOP_PARTIES_URL="http://localhost:5003"
export FSPIOP_QUOTES_URL="http://localhost:5003"
export FSPIOP_TRANSFERS_URL="http://localhost:5003"

# Run sample connector (uncomment and adjust classpath/main if needed)
# java -cp "./*:./lib/*" org.mojave.connector.sample.SampleConnectorApplication

echo "FSP1 environment variables exported."

java -cp "./*:./lib/*" -Dsnowflake.nodeId=1 -Dlog4j.configurationFile=classpath:fsp1-log4j2.xml org.mojave.connector.sample.SampleConnectorApplication
